import React , {useContext} from 'react'
import { Row, Col } from 'antd';

import {CartContext} from '../Context/CartContext'

import Cart from '../components/Cart'


const CartPage = () => {
    const [cart , setCart] = useContext(CartContext)
    const totalPrice = cart.reduce((acc,curr) => acc + curr.totalprice , 0)
    return (
        <div>
          <Cart cartLength={cart.length} totalprice={totalPrice}/><br/>
          <Row className="">
            <Col span={8}><h3>สินค้า</h3></Col>
            <Col span={7}></Col>
            <Col span={3}>ราคา</Col>
            <Col span={3}>จำนวน</Col>
            <Col span={3}>ราคารวม</Col>
          </Row>
          <hr/>
          {
            cart.map(cart =>(
              <div key={cart.id}>ชื่อ : {cart.name} , จำนวน : {cart.count} , ราคารวม : {cart.totalprice}</div>
            ))
          }
        </div>
    )
}

export default CartPage
