import React from 'react'
import { Row, Col } from 'antd';

import Tshirt from '../components/Tshirt'
import CartPage from './CartPage'

const ProductPage = () => {

    const database = [
        { name : "red tshirt " , img: "https://4.imimg.com/data4/AE/MC/MY-1044551/plain-t-shirt-500x500.jpg" , price:150 , id : 1},
        { name : "yellow tshirt " , img: "http://cdn.shopify.com/s/files/1/0232/3963/products/plain-yellow-tshirt_grande.jpg?v=1449214453" , price:180 , id : 2},
        { name : "green tshirt ", img:"https://www.kidswholesaleclothing.co.uk/4131-large_default/baby-blanks-short-sleeve-tshirt-emerald.jpg" , price:200 , id : 3}
    ]

    return (
        <div>
            <Row>
            {
                database.map(item =>(
                    <Col span={8}>
                    <Tshirt id={item.id} name={item.name} price={item.price} img={item.img} key={item.id}  />
                    </Col>
                ))
            }

            </Row>

        </div>
    )
}

export default ProductPage
